import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';
import API_BASE_URL from './Config';
import { AuthContext } from './AuthContext';

const LoginForm = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [account, setAccount] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSignupClick = () => {
    navigate('/signup');
  };

  const handleLogin = async (event) => {
    event.preventDefault();
    setError('');
    
    try {
      const response = await axios.post(`${API_BASE_URL}/doctor/login`, {
        account,
        password,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true,  // 쿠키와 함께 자격 증명을 전송
      });
  
      // 응답 데이터를 직접 접근 (axios는 자동으로 JSON 파싱을 해줌)
      const data = response.data;
  
      // x-refresh-token 헤더 확인 (해당 헤더가 실제로 존재한다면)
      const refreshToken = response.headers['x-refresh-token'];
  
      console.log('Login response:', response);
      console.log('Received Refresh Token:', refreshToken);
  
      if (response.status === 200) {
        login(data.accessToken, refreshToken);
        navigate('/mainscreen'); // 로그인 성공 시 메인 스크린으로 이동
      } else {
        setError(data.message || '로그인에 실패했습니다.');
      }
    } catch (error) {
      setError('서버와의 통신에 실패했습니다.');
      console.error('Error during login:', error);
    }
  };
  

  return (
    <div className="login-form">
      <h2>로그인</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>아이디</label>
          <input
            type="text"
            placeholder="아이디"
            value={account}
            onChange={(e) => setAccount(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>비밀번호</label>
          <input
            type="password"
            placeholder="비밀번호"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="login-button">로그인</button>
        <div className="form-footer">
          <button type="button" className="footer-button">아이디 찾기</button>
          <button type="button" className="footer-button">PW 찾기</button>
        </div>
        <div className="form-footer">
          <button type="button" className="footer-button" onClick={handleSignupClick}>회원가입</button>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
