import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SignUp2.css';
import API_BASE_URL from './Config';

const SignUpFormStep2 = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  const { licenseNumber, account, password } = location.state || {};
  
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [profileImage, setProfileImage] = useState('');
  const [email, setEmail] = useState('');
  const [birth, setBirth] = useState('');
  const [specialty, setSpecialty] = useState('');
  const [specialtyOptions, setSpecialtyOptions] = useState([]);
  const [professionalBackground, setProfessionalBackground] = useState('');
  const [agree, setAgree] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Specialty 데이터를 가져오는 함수
  useEffect(() => {
    const fetchSpecialties = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/doctor/specialty`, {
          method: 'GET',
        });
        const data = await response.json();
        setSpecialtyOptions(data); // Specialty 옵션을 상태로 저장
      } catch (error) {
        console.error('Error fetching specialties:', error);
      }
    };

    fetchSpecialties();
  }, []);

  const handleProfileImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!name || !phone || !email || !birth || !specialty || !agree) {
      setError('모든 필드를 채워주시고, 개인정보 이용에 동의해주세요.');
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/doctor/signup`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          licenseNumber,
          account,
          password,
          name,
          phone,
          profileImage,
          email,
          birth,
          specialty,
          professional_background: professionalBackground,
        }),
      });

      if (response.ok) {
        setSuccess('회원가입이 완료되었습니다. 잠시 후 로그인 페이지로 이동합니다.');
        setError('');
        setTimeout(() => {
          navigate('/login');
        }, 3000);
      } else {
        setError('회원가입에 실패했습니다.');
        setSuccess('');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('서버와의 통신에 실패했습니다.');
      setSuccess('');
    }
  };

  return (
    <div className="sign-up-form">
      <h2>회원 가입 - 2차 정보 입력</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>이름</label>
          <input
            type="text"
            placeholder="이름"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>전화번호</label>
          <input
            type="text"
            placeholder="전화번호"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>프로필 이미지</label>
          <div className="input-container">
            <input
              type="file"
              accept="image/*"
              onChange={handleProfileImageChange}
            />
            {profileImage && <img src={profileImage} alt="Profile Preview" />}
          </div>
        </div>
        <div className="form-group">
          <label>이메일</label>
          <input
            type="email"
            placeholder="이메일"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>생년월일</label>
          <input
            type="date"
            value={birth}
            onChange={(e) => setBirth(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>전문 분야</label>
          <select 
            value={specialty} 
            onChange={(e) => setSpecialty(e.target.value)}
          >
            <option value="">선택하세요</option>
            {specialtyOptions.map((option) => (
              <option key={option.id} value={option.name}>
                {option.name}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>전문 경력</label>
          <textarea
            placeholder="전문 경력"
            value={professionalBackground}
            onChange={(e) => setProfessionalBackground(e.target.value)}
          />
        </div>
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="agree"
            checked={agree}
            onChange={(e) => setAgree(e.target.checked)}
          />
          <label htmlFor="agree">개인정보 이용에 동의합니다.</label>
        </div>
        {!agree && (
          <p className="error-message">개인정보 이용에 동의해주셔야 합니다.</p>
        )}
        <button type="submit">가입 완료</button>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </form>
    </div>
  );
};

export default SignUpFormStep2;
