import React, { useState } from 'react';
import './HistoryPage.css';
import HistoryItem from './HistoryItem';
import EditHistoryItem from './EditHistoryItem';
import { useNavigate } from 'react-router-dom';

const HistoryPage = () => {
  const [historyItems, setHistoryItems] = useState([
    {
      id: 1,
      date: '2024.03.16',
      description: '일자 정재욱 환자(비염 및 축농증)',
      code: '#22345X',
      lastModified: '2024.03.17',
      content: '차도가 어느 정도 있으나, 꾸준히 항생제 복용 필요.'
    },
    {
      id: 2,
      date: '2024.03.11',
      description: '일자 정재욱 환자(비염 및 축농증)',
      code: '#22345X',
      lastModified: '2024.03.13',
      content: '증상 호전 중, 추가 치료 필요 없음.'
    },
    {
      id: 3,
      date: '2024.02.24',
      description: '일자 정재욱 환자(인플루엔자)',
      code: '#14233K',
      lastModified: '2024.03.13',
      content: '인플루엔자 완치, 예방 접종 필요.'
    }
  ]);

  const [editItem, setEditItem] = useState(null);
  const navigate = useNavigate();

  const handleEdit = (item) => {
    navigate(`/history/${item.id}`, { state: { item, isEditing: true } });
  };

  const handleDelete = (id) => {
    setHistoryItems(historyItems.filter(item => item.id !== id));
  };

  const handleSave = (editedItem) => {
    if (editedItem.id) {
      setHistoryItems(historyItems.map(item => (item.id === editedItem.id ? editedItem : item)));
    } else {
      editedItem.id = Date.now();
      setHistoryItems([...historyItems, editedItem]);
    }
    setEditItem(null);
  };

  const handleAdd = () => {
    const newItem = {
      id: null,
      date: '',
      description: '',
      code: '',
      lastModified: new Date().toISOString().split('T')[0],
      content: ''
    };
    setEditItem(newItem);
  };

  const handleItemClick = (item) => {
    navigate(`/history/${item.id}`, { state: { item } });
  };

  return (
    <div className="history-page">
      <header className="history-header">
        <h1>진료 이력 확인</h1>
        <div className="header-controls">
          <input type="text" placeholder="검색하기..." />
          <button className="add-button" onClick={handleAdd}>
            <i className="fas fa-plus"></i> 새 진료 추가하기
          </button>
        </div>
      </header>
      <div className="history-list">
        {historyItems.map((item, index) => (
          <HistoryItem key={index} item={item} onClick={() => handleItemClick(item)} onEdit={() => handleEdit(item)} onDelete={() => handleDelete(item.id)} />
        ))}
      </div>
      {editItem && <EditHistoryItem item={editItem} onSave={handleSave} onCancel={() => setEditItem(null)} />}
    </div>
  );
};

export default HistoryPage;
