import React, { useState } from 'react';
import API_BASE_URL from './Config';
import './AddPatientModal.css'; // 스타일링을 위한 CSS 파일

const AddPatientModal = ({ isOpen, onClose, onAddPatient }) => {
  const [uuid, setUuid] = useState('');
  const [message, setMessage] = useState('');

  const handleAddPatient = async () => {
    if (!uuid) {
      setMessage('UUID를 입력해주세요.');
      return;
    }

    try {
      // 요청 전 로그 출력
      console.log('Request URL:', `${API_BASE_URL}/doctor/patient`);
      console.log('Request Headers:', {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
      });
      console.log('Request Body:', { uuid });

      const response = await fetch(`${API_BASE_URL}/doctor/patient`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        },
        body: JSON.stringify({ uuid }),
      });

      // 응답 후 로그 출력
      console.log('Response Status:', response.status);
      console.log('Response Headers:', response.headers);

      const responseData = await response.json();
      console.log('Response Data:', responseData);

      if (response.ok) {
        setMessage('유효한 UUID 입니다!');
        onAddPatient(); // 환자 추가 후 콜백 호출
        onClose(); // 모달 닫기
      } else {
        setMessage(responseData.message || '환자 추가에 실패했습니다. 다시 시도해주세요.');
      }
    } catch (error) {
      console.error('Error adding patient:', error);
      setMessage('서버와의 통신에 실패했습니다.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close-icon" onClick={onClose}>×</button>
        <h2>환자 추가하기</h2>
        <p>해당 칸에 UUID를 입력해주세요</p>
        <input
          type="text"
          value={uuid}
          onChange={(e) => setUuid(e.target.value)}
          placeholder="UUID 입력"
          className="modal-input"  // 스타일 클래스 추가
        />
        {message && <p className="modal-message">{message}</p>}
        <button onClick={handleAddPatient} className="modal-button">등록하기</button>
      </div>
    </div>
  );
};

export default AddPatientModal;
