// HeartRateChart.js
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const HeartRateChart = ({ heartRateData }) => {
  const labels = heartRateData.content.map(item => item.startAt);
  const minData = heartRateData.content.map(item => item.minimum);
  const maxData = heartRateData.content.map(item => item.maximum);
  const avgData = heartRateData.content.map(item => item.average);

  const lineData = {
    labels,
    datasets: [
      {
        label: 'Minimum Heart Rate',
        data: minData,
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
      },
      {
        label: 'Maximum Heart Rate',
        data: maxData,
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
      },
      {
        label: 'Average Heart Rate',
        data: avgData,
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Heart Rate Over Time',
      },
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MM/DD',
          },
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Heart Rate (bpm)',
        },
      },
    },
  };

  return <Line data={lineData} options={options} />;
};

export default TripleChart;
