import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from './AuthContext';
import API_BASE_URL from './Config';

const PatientList = ({ searchTerm, searchBy, sortBy, showFavorites }) => {
  const { authFetch } = useContext(AuthContext);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPatients = async () => {
      setLoading(true);
      try {
        let searchQuery = '';
        if (searchTerm) {
          searchQuery = searchBy === 'uuid' ? `&uuid=${searchTerm}` : `&name=${searchTerm}`;
        }

        const sortQuery = `${sortBy},${showFavorites ? 'desc' : 'asc'}`;
        const response = await authFetch(`${API_BASE_URL}/doctor/patient?sort=${sortQuery}${searchQuery}`, {
          method: 'GET',
        });

        console.log('Full Response:', response); // 전체 응답을 콘솔에 출력하여 확인
        setPatients(response.content || []); // 응답이 없을 경우 빈 배열로 설정
      } catch (error) {
        console.error('Error fetching patients:', error);
        setPatients([]); // 에러 발생 시 빈 배열로 설정
      } finally {
        setLoading(false);
      }
    };

    fetchPatients();
  }, [searchTerm, searchBy, sortBy, showFavorites, authFetch]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!patients || patients.length === 0) {
    return <p>아직 콘텐츠가 없습니다.</p>;
  }

  return (
    <ul className="patient-list">
      {patients.map(patient => (
        <li key={patient.uuid} className="patient-item">
          <p>UUID: {patient.uuid}</p>
          <p>성별: {patient.gender === 'FEMALE' ? '여성' : '남성'}</p>
          <p>이름: {patient.name}</p>
          <p>연락처: {patient.phone}</p>
          <p>이메일: {patient.email}</p>
        </li>
      ))}
    </ul>
  );
};

export default PatientList;
