// HealthCharts.js
import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../AuthContext';
import API_BASE_URL from '../Config';
import SingleChart from '../Chart/SingleChart';
import DoubleChart from '../Chart/DoubleChart';
import TripleChart from '../Chart/TripleChart';

const HealthCharts = () => {
  const { id } = useParams();
  const { authFetch } = useContext(AuthContext);
  const [ecgData, setEcgData] = useState(null);
  const [hrvData, setHrvData] = useState(null);
  const [rrData, setRrData] = useState(null);
  const [oxygenSaturationData, setOxygenSaturationData] = useState(null);
  const [heartRateData, setHeartRateData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [metricPeriod, setMetricPeriod] = useState('LAST_MONTH');

  // 데이터를 fetch하는 공통 함수
  const fetchMeasurementData = async (measurementType, setDataCallback, responseKey) => {
    try {
      const response = await authFetch(
        `${API_BASE_URL}/doctor/${measurementType}/${id}?page=&size=&metricPeriod=${metricPeriod}`, 
        {
          method: 'GET',
        }
      );

      if (response.status === 200) {
        const data = response.data;
        setDataCallback(data[responseKey]);
      } else {
        console.error(`Failed to fetch ${measurementType} data:`, response.statusText);
      }
    } catch (error) {
      console.error(`Error fetching ${measurementType} data:`, error);
    } finally {
      setLoading(false);
    }
  };

  // 필요한 데이터를 모두 fetch
  useEffect(() => {
    setLoading(true); // 데이터 fetch 시작 전 로딩 상태로 설정
    fetchMeasurementData('ecg', setEcgData, 'ecgList');
    fetchMeasurementData('hrv', setHrvData, 'hrvList');
    fetchMeasurementData('rr', setRrData, 'rrList');
    fetchMeasurementData('oxygenSaturation', setOxygenSaturationData, 'oxygenSaturationList');
    fetchMeasurementData('heartRate', setHeartRateData, 'heartRateList');
  }, [id, authFetch, metricPeriod]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="health-info-page">
      <h2>건강 데이터</h2>

      {/* 기간 선택 */}
      <div>
        <label>기간 선택: </label>
        <select value={metricPeriod} onChange={(e) => setMetricPeriod(e.target.value)}>
          <option value="LAST_WEEK">지난 주</option>
          <option value="LAST_MONTH">지난 달</option>
          <option value="LAST_THREE_MONTHS">지난 세 달</option>
        </select>
      </div>

      {/* ECG 차트 */}
      {ecgData && <SingleChart data={ecgData} title="ECG Data" />}

      {/* HRV 차트 */}
      {hrvData && <SingleChart data={hrvData} title="HRV Data" />}

      {/* RR 차트 */}
      {rrData && <SingleChart data={rrData} title="RR Data" />}

      {/* 산소포화도 차트 */}
      {oxygenSaturationData && <DoubleChart data={oxygenSaturationData} />}

      {/* 심박수 차트 */}
      {heartRateData && <TripleChart data={heartRateData} />}
    </div>
  );
};

export default HealthCharts;
