// HospitalRequestModal.js
import React, { useEffect, useState } from 'react';
import './HospitalRequestModal.css';

const HospitalRequestModal = ({ hospitalId, isOpen, onClose }) => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (isOpen) {
      fetchRequests();
    }
  }, [isOpen]);

  const fetchRequests = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/doctor/hospital/member/request/${hospitalId}?page=0&size=10&hospitalRequestStatus=PENDING`);
      const data = await response.json();
      setRequests(data.content);
    } catch (error) {
      console.error('Error fetching requests:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>닫기</button>
        <h2>병원 가입 요청 현황</h2>
        {loading ? (
          <p>로딩 중...</p>
        ) : (
          <ul>
            {requests.map((request) => (
              <li key={request.id}>
                <p>의사 이름: {request.doctor.name}</p>
                <p>상태: {request.status}</p>
                <p>요청 날짜: {new Date(request.requestedAt).toLocaleString()}</p>
                {/* 추가적인 정보 표시 */}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default HospitalRequestModal;
