import React, { useState, useEffect, useContext, useCallback } from 'react';
import { AuthContext } from './AuthContext';
import API_BASE_URL from './Config';
import PatientList from './PatientList';
import './MainScreen.css';

const MainScreen = () => {
  const { authFetch } = useContext(AuthContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('name');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('ASC'); // 기본값은 오름차순
  const [showFavorites, setShowFavorites] = useState(false);
  const [patients, setPatients] = useState([]); 
  const [loading, setLoading] = useState(true); 
  const [page, setPage] = useState(0); 
  const [totalPages, setTotalPages] = useState(1); 

  // 환자 데이터를 가져오는 함수 (검색 및 정렬 포함)
  const fetchPatients = useCallback(async () => {
    setLoading(true);
    setPatients([]); // 이전 데이터 초기화
    try {
      let searchQuery = '';
      if (searchTerm) {
        searchQuery = searchBy === 'uuid' ? `&uuid=${searchTerm}` : `&name=${searchTerm}`;
      }

      const sortQuery = `${sortBy},${sortOrder}`;
      const response = await authFetch(`${API_BASE_URL}/doctor/patient?page=${page}&size=5&sort=${sortQuery}${searchQuery}`, {
        method: 'GET',
      });
      setPatients(response.data.content);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      console.error('Error fetching patients:', error);
    } finally {
      setLoading(false);
    }
  }, [page, sortBy, sortOrder, searchBy, searchTerm, authFetch]);

  useEffect(() => {
    fetchPatients();  // 데이터 가져오기
  }, [fetchPatients]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchByChange = (event) => {
    setSearchBy(event.target.value);
    if (event.target.value !== 'id') {
      setSortOrder('ASC');  // 'id'가 아닐 때는 기본값을 오름차순으로 설정
    }
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
  };

  const handleSortOrderChange = () => {
    setSortOrder((prevOrder) => (prevOrder === 'ASC' ? 'DESC' : 'ASC'));
  };

  const toggleShowFavorites = () => {
    setShowFavorites(!showFavorites);
  };

  const handleNextPage = () => {
    if (page < totalPages - 1) {
      setPage(page + 1);
    }
  };

  const handlePreviousPage = () => {
    if (page > 0) {
      setPage(page - 1);
    }
  };

  return (
    <div className="main-screen">
      <header className="main-header">
        <button className="add-patient-button">+ 새 환자 추가하기</button>
        <div className="search-container">
          <select className="search-select" value={searchBy} onChange={handleSearchByChange}>
            <option value="name">이름</option>
            <option value="uuid">UUID</option>
          </select>
          <input
            type="text"
            className="search-input"
            placeholder="검색하기..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
        <div className="sort-container">
          <span className="sort-label">정렬순:</span>
          <select className="sort-select" value={sortBy} onChange={handleSortByChange}>
            <option value="name">이름</option>
            <option value="uuid">UUID</option>
            <option value="registrationDate">등록 날짜</option>
          </select>
          <button className="sort-order-button" onClick={handleSortOrderChange}>
            {sortOrder === 'ASC' ? '오름차순' : '내림차순'}
          </button>
        </div>
        <button className="favorites-button" onClick={toggleShowFavorites}>
          {showFavorites ? '전체 보기' : '즐겨찾기 보기'}
        </button>
      </header>
      <div className="patient-container">
        {loading ? (
          <p>Loading...</p>
        ) : patients.length === 0 ? (
          <p>아직 콘텐츠가 없습니다.</p>
        ) : (
          <>
            <PatientList patients={patients} searchTerm={searchTerm} searchBy={searchBy} sortBy={sortBy} showFavorites={showFavorites} />
            <div className="pagination">
              <button onClick={handlePreviousPage} disabled={page === 0}>이전</button>
              <span>페이지 {page + 1} / {totalPages}</span>
              <button onClick={handleNextPage} disabled={page >= totalPages - 1}>다음</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default MainScreen;
