import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API_BASE_URL from './Config'; // Config ÌååÏùºÏóêÏÑú API_BASE_URLÏùÑ Í∞ÄÏ†∏ÏòµÎãàÎã§.
import './PatientItem.css';

const PatientItem = ({ patient, key, toggleFavorite, deletePatient }) => {
  const navigate = useNavigate();
  const [isFavorite, setIsFavorite] = useState(false); // Ï¶êÍ≤®Ï∞æÍ∏∞ Ïó¨Î∂ÄÎ•º ÏÉÅÌÉúÎ°ú Í¥ÄÎ¶¨Ìï©ÎãàÎã§.

  // ÌôòÏûêÏùò Ï¶êÍ≤®Ï∞æÍ∏∞ Ïó¨Î∂ÄÎ•º APIÎ•º ÌÜµÌï¥ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
  const checkFavoriteStatus = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/doctor/patient/favorite/${patient.id}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        },
      });
      const status = await response.text();
      setIsFavorite(status === 'FAVORITE'); // 'FAVORITE'Ïù¥Î©¥ true, 'NOT_FAVORITE'Ïù¥Î©¥ false
    } catch (error) {
      console.error('Ï¶êÍ≤®Ï∞æÍ∏∞ Ïó¨Î∂Ä ÌôïÏù∏ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
    }
  };

  useEffect(() => {
    checkFavoriteStatus();
  }, []); // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏Îê† Îïå Ï¶êÍ≤®Ï∞æÍ∏∞ Ïó¨Î∂ÄÎ•º ÌôïÏù∏Ìï©ÎãàÎã§.

  const handleHistoryClick = () => {
    navigate(`/history/${patient.uuid}`);
  };

  const handleInfoClick = () => {
    navigate(`/health-info/${patient.uuid}`);
  };

  return (
    <div className="patient-item">
      <div className="patient-infobutton">
        <div className="patient-details">
          <span className="patient-name">{patient.name}</span>
          <span className="patient-id">#{patient.uuid}</span>
        </div>
      </div>
      <div className="patient-actions">
        <button className="action-button" onClick={handleInfoClick}>üìä</button>
        <button className="action-button" onClick={handleHistoryClick}>üìã</button>
        <button className="action-button" onClick={() => toggleFavorite(patient.id)}>
          {isFavorite ? '‚≠ê' : '‚òÜ'}
        </button>
        <button className="action-button" onClick={() => deletePatient(patient.id)}>‚ùå</button>
      </div>
    </div>
  );
};

export default PatientItem;
