import React, { useEffect, useState, useContext, useCallback } from 'react';
import { AuthContext } from './AuthContext';
import API_BASE_URL from './Config';
import PatientItem from './PatientItem'; 
import './PatientList.css';

const PatientList = ({ searchTerm, searchBy, sortBy, sortOrder, page, onNextPage, onPreviousPage, triggerRefresh }) => {
  const { authFetch } = useContext(AuthContext);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalPages, setTotalPages] = useState(1);

  const fetchPatients = useCallback(async () => {
    setLoading(true);
    try {
      let searchQuery = '';
      if (searchTerm) {
        searchQuery = searchBy === 'uuid' ? `&uuid=${searchTerm}` : `&name=${searchTerm}`;
      }

      const sortQuery = `${sortBy},${sortOrder}`;
      const response = await authFetch(`${API_BASE_URL}/doctor/patient?page=${page}&size=5&sort=${sortQuery}${searchQuery}`, {
        method: 'GET',
      });

      console.log('Full Response:', response.data);
      setPatients(response.data.content || []);
      setTotalPages(response.data.totalPages || 1);
    } catch (error) {
      console.error('Error fetching patients:', error);
      setPatients([]);
    } finally {
      setLoading(false);
    }
  }, [authFetch, page, searchBy, searchTerm, sortBy, sortOrder]); // showFavorites는 제거합니다.

  useEffect(() => {
    fetchPatients();
  }, [fetchPatients, triggerRefresh]); // fetchPatients와 triggerRefresh만 의존성 배열에 포함합니다.

  const toggleFavorite = async (patientId, isFavorite) => {
    try {
      const method = isFavorite ? 'DELETE' : 'POST';
      const url = `${API_BASE_URL}/doctor/patient/favorite`;
      
      const response = await authFetch(url, {
        method: method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ patientId }),
      });

      if (response.status === 200 || response.status === 201) {
        fetchPatients();
      } else {
        const errorMessage = response.data ? response.data.message : '알 수 없는 오류가 발생했습니다';
        console.error('즐겨찾기 변경 실패:', errorMessage);
        if (errorMessage) {
          alert(`오류: ${errorMessage}`);
        }
      }
    } catch (error) {
      console.error('즐겨찾기 변경 중 오류 발생:', error);
      if (error.message) {
        alert(`오류: ${error.message}`);
      }
    }
  };

  const deletePatient = async (id) => {
    try {
      const response = await authFetch(`${API_BASE_URL}/doctor/patient/${id}`, {
        method: 'DELETE',
      });

      if (response.status === 200 || response.status === 204) {
        setPatients(prevPatients => prevPatients.filter(patient => patient.id !== id));
      } else {
        const errorMessage = response.data ? response.data.message : 'Unknown error occurred';
        console.error('Failed to delete patient', errorMessage);
        if (errorMessage) {
          alert(`Error: ${errorMessage}`);
        }
      }
    } catch (error) {
      console.error('Error deleting patient:', error);
      if (error.message) {
        alert(`Error: ${error.message}`);
      }
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!patients || patients.length === 0) {
    return <p>등록된 환자가 없습니다.</p>;
  }

  return (
    <div className="patient-list">
      {patients.map(patient => (
        <PatientItem
          key={patient.id}
          patient={patient}
          toggleFavorite={toggleFavorite}
          deletePatient={deletePatient}
        />
      ))}
      <div className="pagination">
        {page > 0 && (
          <button onClick={onPreviousPage} className="pagination-arrow">
            &larr;
          </button>
        )}
        <span className="pagination-info">Page {page + 1}</span>
        {page < totalPages - 1 && (
          <button onClick={onNextPage} className="pagination-arrow">
            &rarr;
          </button>
        )}
      </div>
    </div>
  );
};

export default PatientList;
