import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext';
import API_BASE_URL from '../Config';
import './PatientItem.css';

const PatientItem = ({ patient,toggleFavorite, deletePatient }) => {
  const navigate = useNavigate();
  const { authFetch } = useContext(AuthContext);
  const [isFavorite, setIsFavorite] = useState(false);

  const handleHistoryClick = () => {
    navigate(`/history/${patient.id}`);
  };

  const handleInfoClick = () => {
    navigate(`/health-info/${patient.id}`);
  };

  const fetchFavoriteStatus = async () => {
    try {
      const response = await authFetch(`${API_BASE_URL}/doctor/patient/favorite/${patient.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': `application/json` // ÌïÑÏöî Ïãú ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä
        }
      })
      if (response.status === 200) {
        const favoriteStatus = response.data;
        setIsFavorite(favoriteStatus === 'FAVORITE'); // APIÏùò ÏùëÎãµÏóê Îî∞Îùº ÏÉÅÌÉúÎ•º ÏÑ§Ï†ïÌï©ÎãàÎã§.
      } else {
        console.error('Failed to fetch favorite status:', response.status);
      }
    } catch (error) {
      console.error('Error fetching favorite status:', error);
    }
  };

  useEffect(() => {
    fetchFavoriteStatus(); // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Ï¶êÍ≤®Ï∞æÍ∏∞ ÏÉÅÌÉúÎ•º ÌôïÏù∏Ìï©ÎãàÎã§.
  }, [patient.id]);


  return (
    <div className="patient-item">
      <div className="patient-infobutton">
        <div className="patient-details">
          <span className="patient-name">{patient.name}</span>
          <span className="patient-id">#{patient.uuid}</span>
        </div>
      </div>
      <div className="patient-actions">
        <button className="action-button" onClick={handleInfoClick}>üìä</button>
        <button className="action-button" onClick={handleHistoryClick}>üìã</button>
        <button className="action-button" onClick={() => toggleFavorite(patient.id)}>
          {isFavorite === "FAVORITE" ? '‚≠ê' : '‚òÜ'}
        </button>
        <button className="action-button" onClick={() => deletePatient(patient.id)}>‚ùå</button>
      </div>
    </div>
  );
};

export default PatientItem;
