import React, { createContext, useState, useEffect } from 'react';
import API_BASE_URL from './Config';
import axios from 'axios';

export const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('accessToken'));
  const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken'));
  const [refreshToken, setRefreshToken] = useState(localStorage.getItem('refreshToken'));

  useEffect(() => {
    const storedRefreshToken = localStorage.getItem('refreshToken');
    if (storedRefreshToken) {
      setRefreshToken(storedRefreshToken);
    }
  }, []);

  const login = (accessToken, refreshToken) => {
    setAccessToken(accessToken);
    setRefreshToken(refreshToken);
    localStorage.setItem('accessToken', accessToken);
    localStorage.setItem('refreshToken', refreshToken);
    setIsAuthenticated(true);
  };

  const logout = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/doctor/logout`, {}, {
        headers: {
          'Content-Type': 'application/json',
          'cookie': `refresh-token=${refreshToken}`
        },
      });
      console.log('Logout response:', response);
    } catch (error) {
      console.error('Error logging out:', error);
    } finally {
      setAccessToken(null);
      setRefreshToken(null);
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');
      setIsAuthenticated(false);
    }
  };

  const refreshAccessToken = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/doctor/reissue`, {}, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true, // Refresh Token을 쿠키로 전송
      });

      if (response.status === 201) {
        const newAccessToken = response.data.accessToken;
        setAccessToken(newAccessToken);
        localStorage.setItem('accessToken', newAccessToken);
        return newAccessToken;
      } else {
        logout(); // 재발급 실패 시 로그아웃 처리
      }
    } catch (error) {
      console.error('Error refreshing access token:', error);
      logout(); // 오류 발생 시 로그아웃 처리
    }
    return null; // 토큰 갱신 실패 시 null 반환
  };

  const authFetch = async (url, options = {}) => {
    if (!options.headers) {
      options.headers = {};
    }

    options.headers['Authorization'] = `Bearer ${accessToken}`;

    try {
      let response = await axios(url, options);

      // "유효하지 않은 토큰입니다." 메시지를 검사
      if (response.status === 400 && response.data?.message === '유효하지 않은 토큰입니다.') {
        const newToken = await refreshAccessToken();
        if (newToken) {
          options.headers['Authorization'] = `Bearer ${newToken}`;
          response = await axios(url, options);
        } else {
          logout(); // 재발급 실패 시 로그아웃 처리
        }
      }

      return response;
    } catch (error) {
      console.error('Error in authFetch:', error);
      return error.response;
    }
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, login, logout, authFetch }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
