import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import SignUp from './Components/Signup';
import LoginForm from './Components/Login';
import MainScreen from './Components/MainScreen';
import HistoryPage from './Components/HistoryPage';
import HealthInfoPage from './Components/HealthInfoPage';
import Navbar from './Components/Navbar';
import AuthProvider from './Components/AuthContext'; // AuthProvider 가져오기
import ProtectedRoute from './Components/ProtectedRoute';
import PublicRoute from './Components/PublicRoute';
import Test from './Components/Test';
import HistoryDetail from './Components/HistoryDetail'; // 추가된 부분

const App = () => {
  const [historyItems, setHistoryItems] = useState([
    {
      id: 1,
      date: '2024.03.16',
      description: '일자 정재욱 환자(비염 및 축농증)',
      code: '#22345X',
      lastModified: '2024.03.17',
      content: '차도가 어느 정도 있으나, 꾸준히 항생제 복용 필요.'
    },
    {
      id: 2,
      date: '2024.03.11',
      description: '일자 정재욱 환자(비염 및 축농증)',
      code: '#22345X',
      lastModified: '2024.03.13',
      content: '증상 호전 중, 추가 치료 필요 없음.'
    },
    {
      id: 3,
      date: '2024.02.24',
      description: '일자 정재욱 환자(인플루엔자)',
      code: '#14233K',
      lastModified: '2024.03.13',
      content: '인플루엔자 완치, 예방 접종 필요.'
    }
  ]);

  const handleSave = (editedItem) => {
    if (editedItem.id) {
      setHistoryItems(historyItems.map(item => (item.id === editedItem.id ? editedItem : item)));
    } else {
      editedItem.id = Date.now();
      setHistoryItems([...historyItems, editedItem]);
    }
  };

  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <div className="main-content">
          <Routes>
            <Route element={<PublicRoute />}>
              <Route path="/signup" element={<SignUp />} />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/" element={<LoginForm />} /> {/* 메인 페이지를 로그인 페이지로 설정 */}
            </Route>
            <Route element={<ProtectedRoute />}>
              <Route path="/history" element={<HistoryPage historyItems={historyItems} onSave={handleSave} />} />
              <Route path="/history/:id" element={<HistoryDetail onSave={handleSave} />} /> {/* 수정된 부분 */}
              <Route path="/health-info" element={<HealthInfoPage />} />
              <Route path="/test" element={<Test />} />
              <Route path="/mainscreen" element={<MainScreen />} /> {/* 메인 스크린 경로 추가 */}
            </Route>
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
