import React, { useState, useContext, useEffect } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import './HistoryDetail.css';
import { HistoryContext } from './HistoryContext';
import API_BASE_URL from './Config';
import { AuthContext } from './AuthContext';

const HistoryDetail = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();
  const { handleSave } = useContext(HistoryContext);
  const { authFetch } = useContext(AuthContext);
  const [item, setItem] = useState(state?.item || {});
  const [isEditing, setIsEditing] = useState(state?.isEditing || false);

  useEffect(() => {
    if (!state?.item && id) {
      const fetchHistoryItem = async () => {
        try {
          const response = await authFetch(`${API_BASE_URL}/history/${id}`);
          const data = await response.json();
          setItem(data);
        } catch (error) {
          console.error('Error fetching history item:', error);
        }
      };

      fetchHistoryItem();
    }
  }, [id, state, authFetch]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSaveClick = async () => {
    const updatedItem = {
      ...item,
      modifiedAt: new Date().toISOString() // Update the lastModified date
    };
    await handleSave(updatedItem);
    setIsEditing(false);
    navigate('/history');
  };

  const handleCancel = () => {
    setIsEditing(false);
    navigate('/history');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setItem((prevItem) => ({
      ...prevItem,
      [name]: value
    }));
  };

  const formatDate = (dateString) => {
    return dateString ? dateString.split('T')[0] : '';
  };

  return (
    <div className="history-detail">
      {isEditing ? (
        <>
          <header className="history-detail-header">
            <h1>{item.id ? '진료 이력 수정' : '새 진료 추가'}</h1>
            <div className="history-detail-controls">
              <button className="save-button" onClick={handleSaveClick}>저장</button>
              <button className="cancel-button" onClick={handleCancel}>취소</button>
            </div>
          </header>
          <div className="history-detail-content">
            <div className="history-detail-row">
              <span className="history-detail-label">날짜:</span>
              <input
                type="date"
                name="appointmentAt"
                value={formatDate(item.appointmentAt)}
                onChange={handleChange}
              />
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">병명 태그:</span>
              <input
                type="text"
                name="doctorId"
                value={item.doctorId}
                onChange={handleChange}
              />
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">진료 내용:</span>
              <textarea
                name="diagnosis"
                value={item.diagnosis}
                onChange={handleChange}
                rows="10"
                style={{ width: '100%' }}
              />
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">설명:</span>
              <textarea
                name="description"
                value={item.description}
                onChange={handleChange}
                rows="5"
                style={{ width: '100%' }}
              />
            </div>
          </div>
        </>
      ) : (
        <>
          <header className="history-detail-header">
            <h1>{item.title}</h1>
            <div className="history-detail-controls">
              <button className="edit-button" onClick={handleEdit}>수정</button>
            </div>
          </header>
          <div className="history-detail-content">
            <div className="history-detail-row">
              <span className="history-detail-label">상태:</span>
              <span className="history-detail-value">{item.status || '치료 진행 중'}</span>
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">병명 태그:</span>
              <span className="history-detail-value">{item.doctorId}</span>
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">환자명:</span>
              <span className="history-detail-value">{item.patientName || '정재욱'}</span>
            </div>
            <div className="history-detail-row">
              <span className="history-detail-label">진료 내용:</span>
              <span className="history-detail-value">{item.diagnosis}</span>
            </div>
            <div className="history-detail-description">{item.description}</div>
            <div className="history-detail-footer">
              <span>마지막 수정: {formatDate(item.modifiedAt)}</span>
              <div className="file-links">
                <a href="#/path/to/file1.pdf">진단서.pdf</a>
                <a href="#/path/to/file2.pdf">2024.05.01 정재욱 처방전.pdf</a>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default HistoryDetail;
