// OxygenSaturationChart.js
import React from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar, Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const OxygenSaturationChart = ({ oxygenSaturationData, dailyAverage }) => {
  const labels = oxygenSaturationData.content.map(item => item.measuredAt);
  const o2SatData = oxygenSaturationData.content.map(item => item.o2Sat * 100); // 퍼센트로 변환
  const atmData = oxygenSaturationData.content.map(item => item.atm);

  const barData = {
    labels,
    datasets: [
      {
        type: 'bar',
        label: 'Oxygen Saturation (%)',
        data: o2SatData,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderWidth: 1,
      },
      {
        type: 'line',
        label: 'Atmospheric Pressure (atm)',
        data: atmData,
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Oxygen Saturation and Atmospheric Pressure',
      },
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MM/DD',
          },
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Values',
        },
      },
    },
  };

  return <Bar data={barData} options={options} />;
};

export default DoubleChart;
