import React, { useState } from 'react';
import PatientList from './PatientList';
import './MainScreen.css';

const MainScreen = () => {
  const [patients, setPatients] = useState([
    { name: '정재욱', id: '#XOEIF5GJ', registrationDate: '2024-01-01' },
    { name: '여은동', id: '#EXFQ921K', registrationDate: '2024-02-15' },
    { name: '현뚱따', id: '#MAPLEII16', registrationDate: '2024-03-20' },
    { name: '정진우', id: '#DARKYETI', registrationDate: '2024-01-10' }
  ]);
  const [sortCriteria, setSortCriteria] = useState('name');

  const handleSortChange = (e) => {
    const criteria = e.target.value;
    setSortCriteria(criteria);

    const sortedPatients = [...patients].sort((a, b) => {
      if (criteria === 'name') {
        return a.name.localeCompare(b.name);
      } else if (criteria === 'id') {
        return a.id.localeCompare(b.id);
      } else if (criteria === 'registrationDate') {
        return new Date(a.registrationDate) - new Date(b.registrationDate);
      } else {
        return 0;
      }
    });

    setPatients(sortedPatients);
  };

  return (
    <div className="main-screen">
      <header className="main-header">
        <button className="add-patient-button">+ 새 환자 추가하기</button>
        <div className="search-container">
          <input type="text" className="search-input" placeholder="검색하기..." />
          <select className="sort-select" value={sortCriteria} onChange={handleSortChange}>
            <option value="name">이름</option>
            <option value="id">UUID</option>
            <option value="registrationDate">등록 날짜</option>
          </select>
        </div>
      </header>
      <div className="patient-container">
        <PatientList patients={patients} />
      </div>
    </div>
  );
};

export default MainScreen;
