import React from 'react';
import './PatientList.css';

const PatientList = ({ searchTerm, searchBy }) => {
  const patients = [
    { id: 1, name: '정재욱', condition: '비염', tag: '#XOEIF5GJ' },
    { id: 2, name: '여은동', condition: '감기', tag: '#EXFQ921K' },
    // 추가 환자 데이터를 여기에 추가하세요
  ];

  const filteredPatients = patients.filter((patient) => {
    if (searchBy === 'name') {
      return patient.name.includes(searchTerm);
    } else if (searchBy === 'id') {
      return patient.id.toString().includes(searchTerm);
    } else if (searchBy === 'condition') {
      return patient.condition.includes(searchTerm);
    }
    return false;
  });

  return (
    <div className="patient-list">
      {filteredPatients.map((patient) => (
        <div key={patient.id} className="patient-card">
          <div className="patient-info">
            <h3>{patient.name}</h3>
            <p>{patient.tag}</p>
          </div>
          <div className="patient-actions">
            {/* 여기에 환자별로 수행할 작업 버튼을 추가하세요 */}
          </div>
        </div>
      ))}
    </div>
  );
};

export default PatientList;
