import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale, // 추가된 부분: TimeScale 등록
} from 'chart.js';

import 'chartjs-adapter-date-fns'; // 추가된 부분: Date 어댑터를 import

const DoubleChart = ({ oxygenSaturationData }) => {

  if (!oxygenSaturationData || !oxygenSaturationData.content) {
    console.log('Data:', oxygenSaturationData )
    return <p>데이터를 불러오는 중 문제가 발생했습니다.</p>;
  }

  // 데이터 가공
  const labels = oxygenSaturationData.content.map(item => new Date(item.measuredAt));
  const o2SatData = oxygenSaturationData.content.map(item => item.o2Sat * 100); // 퍼센트로 변환
  const atmData = oxygenSaturationData.content.map(item => item.atm);

  // 차트 데이터 현황 출력
  console.log('Labels:', labels);
  console.log('Oxygen Saturation Data:', o2SatData);
  console.log('Atmospheric Pressure Data:', atmData);

  const barData = {
    labels,
    datasets: [
      {
        type: 'bar',
        label: 'Oxygen Saturation (%)',
        data: o2SatData,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderWidth: 1,
        yAxisID: 'y1',
      },
      {
        type: 'line',
        label: 'Atmospheric Pressure (atm)',
        data: atmData,
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
        yAxisID: 'y2',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Oxygen Saturation and Atmospheric Pressure',
      },
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MM/dd',
          },
        },
        title: {
          display: true,
          text: 'Date',
        },
      },
      y1: {
        type: 'linear',
        position: 'left',
        title: {
          display: true,
          text: 'Oxygen Saturation (%)',
        },
        ticks: {
          beginAtZero: true,
        },
      },
      y2: {
        type: 'linear',
        position: 'right',
        title: {
          display: true,
          text: 'Atmospheric Pressure (atm)',
        },
        ticks: {
          beginAtZero: true,
        },
        grid: {
          drawOnChartArea: false, // 오른쪽 축 그리드를 없앱니다.
        },
      },
    },
  };

  return <Bar data={barData} options={options} />;
};

export default DoubleChart;
