import React from 'react';
import PatientItem from './PatientItem';
import './PatientList.css';

const PatientList = ({ searchTerm, searchBy, sortBy }) => {
  const patients = [
    { name: '정재욱', id: '#XOEIF5GJ', registrationDate: '2024-01-01' },
    { name: '여은동', id: '#EXFQ921K', registrationDate: '2024-02-15' },
    { name: '현뚱따', id: '#MAPLEII16', registrationDate: '2024-03-20' },
    { name: '정진우', id: '#DARKYETI', registrationDate: '2024-01-10' }
  ];

  const filteredPatients = patients
    .filter((patient) => {
      if (searchBy === 'name') {
        return patient.name.includes(searchTerm);
      } else if (searchBy === 'id') {
        return patient.id.includes(searchTerm);
      }
      return false;
    })
    .sort((a, b) => {
      if (sortBy === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortBy === 'id') {
        return a.id.localeCompare(b.id);
      } else if (sortBy === 'registrationDate') {
        return new Date(a.registrationDate) - new Date(b.registrationDate);
      }
      return 0;
    });

  return (
    <div className="patient-list">
      {filteredPatients.map((patient, index) => (
        <PatientItem key={index} patient={patient} />
      ))}
    </div>
  );
};

export default PatientList;
