import React, { useState, useContext, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Login.css';
import API_BASE_URL from './Config';
import { AuthContext } from './AuthContext';

const LoginForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { login, isAuthenticated } = useContext(AuthContext);
  const [account, setAccount] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/mainscreen');
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    if (location.state && location.state.alert) {
      alert(location.state.alert);
      navigate(location.pathname, { replace: true, state: {} }); // Reset the alert state
    }
  }, [location, navigate]);

  const handleSignupClick = () => {
    navigate('/signup');
  };

  const handleLogin = async (event) => {
    event.preventDefault();
    setError('');

    try {
      const response = await fetch(`${API_BASE_URL}/doctor/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          account,
          password,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        const refreshToken = response.headers.get('x-refresh-token');
        login(data.accessToken, refreshToken);
        navigate('/mainscreen');
      } else {
        setError(data.message || '로그인에 실패했습니다.');
      }
    } catch (error) {
      setError('서버와의 통신에 실패했습니다.');
    }
  };

  return (
    <div className="login-form">
      <h2>로그인</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>아이디</label>
          <input
            type="text"
            placeholder="아이디"
            value={account}
            onChange={(e) => setAccount(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>비밀번호</label>
          <input
            type="password"
            placeholder="비밀번호"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="login-button">로그인</button>
        <div className="form-footer">
          <button type="button" className="footer-button">아이디 찾기</button>
          <button type="button" className="footer-button">PW 찾기</button>
        </div>
        <div className="form-footer">
          <button type="button" className="footer-button" onClick={handleSignupClick}>회원가입</button>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
