import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from './AuthContext';
import API_BASE_URL from './Config';
import PatientItem from './PatientItem'; // PatientItem을 참조합니다.
import './PatientList.css';

const PatientList = ({ searchTerm, searchBy, sortBy, showFavorites }) => {
  const { authFetch } = useContext(AuthContext);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPatients = async () => {
      setLoading(true);
      try {
        let searchQuery = '';
        if (searchTerm) {
          searchQuery = searchBy === 'uuid' ? `&uuid=${searchTerm}` : `&name=${searchTerm}`;
        }

        const sortQuery = `${sortBy},${showFavorites ? 'desc' : 'asc'}`;
        const response = await authFetch(`${API_BASE_URL}/doctor/patient?sort=${sortQuery}${searchQuery}`, {
          method: 'GET',
        });

        console.log('Full Response:', response.data);
        setPatients(response.data.content || []);
      } catch (error) {
        console.error('Error fetching patients:', error);
        setPatients([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPatients();
  }, [searchTerm, searchBy, sortBy, showFavorites, authFetch]);

  const toggleFavorite = async (patientId) => {
    // 즐겨찾기 토글 로직 (API를 통한 업데이트 등)
  };

  const deletePatient = async (id) => {
    try {
      const response = await authFetch(`${API_BASE_URL}/doctor/patient/${id}`, {
        method: 'DELETE',
      });

      if (response.status === 200 || response.status === 204) {
        setPatients(prevPatients => prevPatients.filter(patient => patient.id !== id));
      } else {
        const errorMessage = response.data ? response.data.message : 'Unknown error occurred';
        console.error('Failed to delete patient', errorMessage);
        if (errorMessage) {
          alert(`Error: ${errorMessage}`);
        }
      }
    } catch (error) {
      console.error('Error deleting patient:', error);
      if (error.message) {
        alert(`Error: ${error.message}`);
      }
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!patients || patients.length === 0) {
    return <p>아직 콘텐츠가 없습니다.</p>;
  }

  return (
    <div className="patient-list">
      {patients.map(patient => (
        <PatientItem
          key={patient.uuid}
          patient={patient}
          toggleFavorite={toggleFavorite}
          deletePatient={deletePatient}
        />
      ))}
    </div>
  );
};

export default PatientList;
