import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../AuthContext';
import API_BASE_URL from '../Config';
import SingleChart from '../Chart/SingleChart';
import DoubleChart from '../Chart/DoubleChart';
import TripleChart from '../Chart/TripleChart';

const HealthCharts = () => {
  const { id } = useParams();
  const { authFetch } = useContext(AuthContext);
  const [ecgData, setEcgData] = useState(null);
  const [hrvData, setHrvData] = useState(null);
  const [rrData, setRrData] = useState(null);
  const [oxygenSaturationData, setOxygenSaturationData] = useState(null);
  const [heartRateData, setHeartRateData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [metricPeriod, setMetricPeriod] = useState('LAST_MONTH');

  // 통합 데이터 fetch 함수
  const fetchMeasurementData = async (measurementType, setDataCallback, responseKey) => {
    try {
      const response = await authFetch(
        `${API_BASE_URL}/doctor/${measurementType}/${id}?page=&size=&metricPeriod=${metricPeriod}`, 
        {
          method: 'GET',
        }
      );

      if (response.status === 200) {
        const data = response.data;
        if (data && data[responseKey] && data[responseKey].content) {
          setDataCallback(data[responseKey]);
        } else {
          console.error(`Data missing in response for ${measurementType}`);
          setDataCallback({ content: [] }); // 데이터가 없을 때 빈 배열로 초기화
        }
      } else {
        console.error(`Failed to fetch ${measurementType} data:`, response.statusText);
      }
    } catch (error) {
      console.error(`Error fetching ${measurementType} data:`, error);
      setDataCallback({ content: [] }); // 에러 발생 시 빈 배열로 초기화
    }
  };

  useEffect(() => {
    setLoading(true);

    // 모든 fetch 요청을 병렬 처리하여 기다림
    Promise.all([
      fetchMeasurementData('ecg', setEcgData, 'ecgList'),
      fetchMeasurementData('hrv', setHrvData, 'hrvList'),
      fetchMeasurementData('rr', setRrData, 'rrList'),
      fetchMeasurementData('o2Sat', setOxygenSaturationData, 'oxygenSaturationList'),
      fetchMeasurementData('hr', setHeartRateData, 'heartRateList')
    ])
    .then(() => setLoading(false)) // 모든 fetch가 완료되면 로딩 상태 false로 변경
    .catch((error) => {
      console.error('Error in fetching measurement data:', error);
      setLoading(false); // 에러 발생 시 로딩 상태 false로 변경
    });
  }, [id, authFetch, metricPeriod]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="health-info-page">
      <h2>건강 데이터</h2>

      {/* 기간 선택 */}
      <div>
        <label>기간 선택: </label>
        <select value={metricPeriod} onChange={(e) => setMetricPeriod(e.target.value)}>
          <option value="LAST_WEEK">지난 주</option>
          <option value="LAST_MONTH">지난 달</option>
          <option value="LAST_THREE_MONTHS">지난 세 달</option>
        </select>
      </div>

      {/* ECG 차트 */}
      {ecgData && ecgData.content.length > 0 && <SingleChart data={ecgData} title="ECG Data" />}

      {/* HRV 차트 */}
      {hrvData && hrvData.content.length > 0 && <SingleChart data={hrvData} title="HRV Data" />}

      {/* RR 차트 */}
      {rrData && rrData.content.length > 0 && <SingleChart data={rrData} title="RR Data" />}

      {/* 산소포화도 차트 */}
      {oxygenSaturationData && oxygenSaturationData.content.length > 0 && <DoubleChart data={oxygenSaturationData} title="Oxygen Saturation Data" />}

      {/* 심박수 차트 */}
      {heartRateData && heartRateData.content.length > 0 && <TripleChart data={heartRateData} title="Heart Rate Data" />}
    </div>
  );
};

export default HealthCharts;
