import React, { useState, useContext } from 'react';
import API_BASE_URL from './Config';
import { AuthContext } from './AuthContext'; // AuthContext를 가져옵니다.
import './AddPatientModal.css'; // 스타일링을 위한 CSS 파일

const AddPatientModal = ({ isOpen, onClose, onAddPatient }) => {
  const [uuid, setUuid] = useState('');
  const [message, setMessage] = useState('');
  const { refreshAccessToken, logout } = useContext(AuthContext); // AuthContext에서 필요한 메서드를 가져옵니다.

  const handleAddPatient = async () => {
    if (!uuid) {
      setMessage('UUID를 입력해주세요.');
      return;
    }

    try {
      console.log('Request URL:', `${API_BASE_URL}/doctor/patient`);
      console.log('Request Headers:', {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
      });
      console.log('Request Body:', { uuid });

      const response = await fetch(`${API_BASE_URL}/doctor/patient`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        },
        body: JSON.stringify({ uuid }),
      });

      console.log('Response Status:', response.status);

      if (response.status === 401) { // 유효하지 않은 토큰이 응답될 경우
        setMessage('유효하지 않은 토큰입니다. 토큰을 갱신하는 중...');
        const newAccessToken = await refreshAccessToken(); // 토큰 갱신 시도

        if (newAccessToken) {
          // 새 토큰으로 다시 요청 시도
          const retryResponse = await fetch(`${API_BASE_URL}/doctor/patient`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${newAccessToken}`,
            },
            body: JSON.stringify({ uuid }),
          });

          if (retryResponse.ok) {
            setMessage('추가되었습니다!');
            onAddPatient(); // 환자 추가 후 PatientList 새로고침
            onClose(); // 모달 닫기
          } else {
            setMessage('환자 추가에 실패했습니다. 다시 시도해주세요.');
          }
        } else {
          setMessage('토큰 갱신에 실패했습니다. 로그아웃합니다.');
          logout(); // 토큰 갱신 실패 시 로그아웃 처리
        }
      } else if (response.ok) {
        setMessage('추가되었습니다!');
        onAddPatient(); // 환자 추가 후 PatientList 새로고침
        onClose(); // 모달 닫기
      } else {
        setMessage('환자 추가에 실패했습니다. 다시 시도해주세요.');
      }
    } catch (error) {
      console.error('Error adding patient:', error);
      setMessage('서버와의 통신에 실패했습니다.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close-icon" onClick={onClose}>×</button>
        <h2>환자 추가하기</h2>
        <p>해당 칸에 UUID를 입력해주세요</p>
        <input
          type="text"
          value={uuid}
          onChange={(e) => setUuid(e.target.value)}
          placeholder="UUID 입력"
          className="modal-input"
        />
        {message && <p className="modal-message">{message}</p>}
        <button onClick={handleAddPatient} className="modal-button">등록하기</button>
      </div>
    </div>
  );
};

export default AddPatientModal;
